import xlwt

wb = xlwt.Workbook()
ws = wb.add_sheet('Operator chats')

row = 0
ws.write(row, 0, "operator_count")
ws.write(row, 1, "date")
ws.write(row, 2, "chat_count")
row += 1

for date in chats_by_date:
    ws.write(row, 0, date['operator_count'])
    ws.write(row, 1, date['date_created'])
    ws.write(row, 2, date['chat_count'])
    row += 1

wb.save('operator_chats.xls')